import React, { useState } from 'react';
import { createLead } from '../api';

export default function LeadForm({ onAdded }) {
  const [form, setForm] = useState({ name:'', email:'', phone:'', source:'', status:'New' });
  const [errors, setErrors] = useState({});

  const validate = () => {
    const e = {};
    if (!form.name.trim()) e.name = 'Name is required';
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(form.email)) e.email = 'Valid email required';
    if (!/^[0-9]{10}$/.test(form.phone)) e.phone = 'Phone must be 10 digits';
    if (!form.source) e.source = 'Source is required';
    setErrors(e);
    return Object.keys(e).length === 0;
  };

  const submit = async (ev) => {
    ev.preventDefault();
    if (!validate()) return;
    try {
      const res = await createLead(form);
      setForm({ name:'', email:'', phone:'', source:'', status:'New' });
      onAdded && onAdded(res.data);
    } catch (err) {
      console.error(err);
      alert('Failed to save lead');
    }
  };

  return (
    <form onSubmit={submit} className="bg-black bg-opacity-70 backdrop-blur-md rounded-3xl p-8 shadow-lg border border-cyan-700 space-y-6 max-w-md mx-auto">
      {['name', 'email', 'phone', 'source', 'status'].map(field => (
        <InputGroup
          key={field}
          id={field}
          label={field.charAt(0).toUpperCase() + field.slice(1)}
          value={form[field]}
          error={errors[field]}
          options={field === 'source' ? ['Website', 'Referral', 'Ad', 'Other'] : null}
          onChange={e => setForm({ ...form, [field]: e.target.value })}
          placeholder={field === 'phone' ? '10-digit phone' : `Enter ${field}`}
        />
      ))}
      <div className="text-right">
        <button type="submit" className="px-7 py-3 bg-cyan-600 hover:bg-cyan-700 rounded-xl font-semibold text-white shadow-lg focus:ring-4 focus:ring-cyan-400 transition">
          Save Lead
        </button>
      </div>
    </form>
  );
}

function InputGroup({ id, label, value, error, options, onChange, placeholder }) {
  return (
    <div>
      <label htmlFor={id} className="block text-gray-300 mb-2 font-semibold select-none">{label}</label>
      {options ? (
        <select
          id={id}
          value={value}
          onChange={onChange}
          className={`w-full bg-transparent border border-gray-700 rounded-xl px-4 py-2 text-gray-200 placeholder-gray-600 focus:outline-none focus:ring-2 focus:ring-cyan-500 transition ${
            error ? 'border-red-600 focus:ring-red-400' : ''
          }`}
        >
          <option value="">Select {label.toLowerCase()}</option>
          {options.map(opt => <option key={opt} value={opt}>{opt}</option>)}
        </select>
      ) : (
        <input
          type="text"
          id={id}
          value={value}
          onChange={onChange}
          placeholder={placeholder}
          className={`w-full bg-transparent border border-gray-700 rounded-xl px-4 py-2 text-gray-200 placeholder-gray-600 focus:outline-none focus:ring-2 focus:ring-cyan-500 transition ${
            error ? 'border-red-600 focus:ring-red-400' : ''
          }`}
        />
      )}
      {error && <p className="text-red-600 mt-1 text-sm select-none">{error}</p>}
    </div>
  );
}
