import React from 'react';
import LeadForm from './components/LeadForm';
import LeadList from './components/LeadList';

export default function App() {
  const [refreshKey, setRefreshKey] = React.useState(0);

  return (
    <div className="dark min-h-screen bg-gray-900 flex items-center justify-center py-8">
      <div className="w-full max-w-6xl px-4">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-10">
          <div>
            <h2 className="text-2xl font-extrabold mb-6 text-white">Add Lead</h2>
            <LeadForm onAdded={() => setRefreshKey(k => k + 1)} />
          </div>
          <div>
            <h2 className="text-2xl font-extrabold mb-6 text-white">Leads</h2>
            <LeadList refreshKey={refreshKey} />
          </div>
        </div>
      </div>
    </div>
  );
}
