import React from 'react';
import LeadForm from './components/LeadForm';
import LeadList from './components/LeadList';

export default function App() {
  const [refreshKey, setRefreshKey] = React.useState(0);

  return (
    <div className="min-h-screen bg-black text-gray-300 flex items-center justify-center p-8">
      <div className="w-full max-w-7xl grid grid-cols-1 lg:grid-cols-2 gap-12">
        <section>
          <h1 className="text-3xl font-extrabold text-white mb-8 tracking-tight">Add New Lead</h1>
          <LeadForm onAdded={() => setRefreshKey(k => k + 1)} />
        </section>
        <section>
          <h1 className="text-3xl font-extrabold text-white mb-8 tracking-tight">Leads</h1>
          <LeadList refreshKey={refreshKey} />
        </section>
      </div>
    </div>
  );
}
