import React, { useState } from 'react';
import { createLead } from '../api';

export default function LeadForm({ onAdded }) {
  const [form, setForm] = useState({ name: '', email: '', phone: '', source: '', status: 'New' });
  const [errors, setErrors] = useState({});

  const validate = () => {
    const e = {};
    if (!form.name.trim()) e.name = 'Name is required';
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(form.email)) e.email = 'Valid email required';
    if (!/^[0-9]{10}$/.test(form.phone)) e.phone = 'Phone must be 10 digits';
    if (!form.source) e.source = 'Source is required';
    setErrors(e);
    return Object.keys(e).length === 0;
  };

  const submit = async (ev) => {
    ev.preventDefault();
    if (!validate()) return;
    try {
      const res = await createLead(form);
      setForm({ name: '', email: '', phone: '', source: '', status: 'New' });
      onAdded && onAdded(res.data);
    } catch (err) {
      console.error(err);
      alert('Failed to save lead');
    }
  };

  return (
    <form onSubmit={submit} className="bg-gray-800 bg-opacity-90 backdrop-blur p-8 rounded-2xl shadow-lg border border-gray-700 space-y-5 text-gray-200">
      <div>
        <label htmlFor="name" className="block mb-2 font-semibold">Name</label>
        <input
          id="name"
          type="text"
          value={form.name}
          onChange={e => setForm({ ...form, name: e.target.value })}
          className={`w-full rounded-md border px-4 py-2 focus:outline-none focus:ring-2 transition ${
            errors.name ? 'border-red-600 focus:ring-red-400' : 'border-gray-600 focus:ring-blue-500'
          } bg-gray-900 text-gray-100 placeholder-gray-500`}
          placeholder="Enter name"
        />
        {errors.name && <p className="text-red-500 text-xs mt-1">{errors.name}</p>}
      </div>
      <div>
        <label htmlFor="email" className="block mb-2 font-semibold">Email</label>
        <input
          id="email"
          type="email"
          value={form.email}
          onChange={e => setForm({ ...form, email: e.target.value })}
          className={`w-full rounded-md border px-4 py-2 focus:outline-none focus:ring-2 transition ${
            errors.email ? 'border-red-600 focus:ring-red-400' : 'border-gray-600 focus:ring-blue-500'
          } bg-gray-900 text-gray-100 placeholder-gray-500`}
          placeholder="Enter email"
        />
        {errors.email && <p className="text-red-500 text-xs mt-1">{errors.email}</p>}
      </div>
      <div>
        <label htmlFor="phone" className="block mb-2 font-semibold">Phone</label>
        <input
          id="phone"
          type="text"
          value={form.phone}
          onChange={e => setForm({ ...form, phone: e.target.value })}
          className={`w-full rounded-md border px-4 py-2 focus:outline-none focus:ring-2 transition ${
            errors.phone ? 'border-red-600 focus:ring-red-400' : 'border-gray-600 focus:ring-blue-500'
          } bg-gray-900 text-gray-100 placeholder-gray-500`}
          placeholder="10-digit phone"
        />
        {errors.phone && <p className="text-red-500 text-xs mt-1">{errors.phone}</p>}
      </div>
      <div>
        <label htmlFor="source" className="block mb-2 font-semibold">Source</label>
        <select
          id="source"
          value={form.source}
          onChange={e => setForm({ ...form, source: e.target.value })}
          className={`w-full rounded-md border py-2 px-3 focus:outline-none focus:ring-2 transition ${
            errors.source ? 'border-red-600 focus:ring-red-400' : 'border-gray-600 focus:ring-blue-500'
          } bg-gray-900 text-gray-100`}
        >
          <option value="">Select source</option>
          <option>Website</option>
          <option>Referral</option>
          <option>Ad</option>
          <option>Other</option>
        </select>
        {errors.source && <p className="text-red-500 text-xs mt-1">{errors.source}</p>}
      </div>
      <div>
        <label htmlFor="status" className="block mb-2 font-semibold">Status</label>
        <select
          id="status"
          value={form.status}
          onChange={e => setForm({ ...form, status: e.target.value })}
          className="w-full rounded-md border border-gray-600 py-2 px-3 focus:outline-none focus:ring-2 focus:ring-blue-500 bg-gray-900 text-gray-100"
        >
          <option>New</option>
          <option>Contacted</option>
          <option>Qualified</option>
        </select>
      </div>
      <div className="text-right">
        <button
          type="submit"
          className="bg-blue-600 hover:bg-blue-700 focus:ring-2 focus:ring-blue-500 text-white font-semibold rounded-lg px-6 py-2 shadow transition"
        >
          Save Lead
        </button>
      </div>
    </form>
  );
}
