import React from 'react';
import LeadForm from './components/LeadForm';
import { useNavigate } from 'react-router-dom'; // assuming you use react-router for navigation

export default function LeadFormPage() {
  const navigate = useNavigate();

  return (
    <div className="flex min-h-screen bg-gray-900 text-white">
      {/* Left half: Lead Form */}
      <div className="w-1/2 flex flex-col justify-center items-center p-16 bg-gradient-to-b from-black via-gray-900 to-gray-800 shadow-xl">
        <h1 className="text-5xl font-bold mb-12 tracking-wide uppercase text-cyan-400 drop-shadow-lg">
          Add Lead
        </h1>
        <LeadForm />
      </div>

      {/* Right half: Info + Navigation */}
      <div className="w-1/2 flex flex-col justify-center items-center bg-cyan-900 p-24 space-y-10">
        <h2 className="text-6xl font-extrabold text-white tracking-tight max-w-lg text-center drop-shadow-xl">
          Manage Your Leads Easily & Efficiently
        </h2>
        <p className="text-cyan-200 text-lg max-w-md text-center leading-relaxed">
          Streamline your workflow by adding new leads quickly and accessing the complete lead list with ease.
        </p>
        <button
          onClick={() => navigate('/leads')}
          className="mt-auto bg-white text-cyan-900 font-bold px-10 py-4 rounded-full shadow-xl text-xl hover:scale-105 transform transition"
          aria-label="Go to lead list page"
        >
          Go to Lead List
        </button>
      </div>
    </div>
  );
}
