import React, { useEffect, useState } from 'react';
import { getLeads, deleteLead, updateLead } from '../api';

export default function LeadList({ refreshKey }) {
  const [leads, setLeads] = useState([]);
  const [loading, setLoading] = useState(true);
  const [q, setQ] = useState('');

  const fetch = async () => {
    try {
      setLoading(true);
      const res = await getLeads({ q });
      setLeads(res.data.data || res.data);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetch();
  }, [refreshKey]);

  const remove = async (id) => {
    if (!window.confirm('Delete lead?')) return;
    await deleteLead(id);
    setLeads(leads.filter((l) => l._id !== id));
  };

  const changeStatus = async (id, status) => {
    const res = await updateLead(id, { status });
    setLeads(leads.map((l) => (l._id === id ? res.data : l)));
  };

  if (loading) return <div className="p-6 rounded-3xl bg-black bg-opacity-70 text-center text-cyan-400 shadow-lg">Loading...</div>;

  return (
    <div className="bg-black bg-opacity-70 backdrop-blur-md rounded-3xl shadow-lg border border-cyan-700 p-6 max-w-full overflow-x-auto">
      <div className="flex gap-4 mb-6">
        <input
          placeholder="Search name/email/phone"
          value={q}
          onChange={e => setQ(e.target.value)}
          className="flex-grow rounded-xl border border-gray-700 bg-transparent px-4 py-2 text-gray-300 placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-cyan-500 transition"
        />
        <button
          onClick={fetch}
          className="px-5 py-2 bg-cyan-600 hover:bg-cyan-700 rounded-xl text-white font-semibold shadow-lg transition"
        >
          Search
        </button>
      </div>
      <table className="min-w-full text-gray-300">
        <thead className="bg-cyan-900 bg-opacity-30 rounded-lg text-left">
          <tr>
            {['Name', 'Email', 'Phone', 'Source', 'Status', 'Actions'].map((h) => (
              <th key={h} className="p-4 font-semibold tracking-wide select-none">{h}</th>
            ))}
          </tr>
        </thead>
        <tbody className="divide-y divide-gray-800">
          {leads.length === 0 && (
            <tr>
              <td colSpan={6} className="py-8 text-center text-gray-500 select-none">No leads found.</td>
            </tr>
          )}
          {leads.map(lead => (
            <tr key={lead._id} className="hover:bg-cyan-900 hover:bg-opacity-30 transition rounded-lg">
              <td className="p-4">{lead.name}</td>
              <td className="p-4">{lead.email}</td>
              <td className="p-4">{lead.phone}</td>
              <td className="p-4">{lead.source}</td>
              <td className="p-4">
                <select
                  className="bg-transparent border border-gray-700 rounded-lg p-1 text-gray-300 focus:outline-none focus:ring-2 focus:ring-cyan-500 transition"
                  value={lead.status}
                  onChange={e => changeStatus(lead._id, e.target.value)}
                >
                  <option>New</option>
                  <option>Contacted</option>
                  <option>Qualified</option>
                </select>
              </td>
              <td className="p-4">
                <button
                  onClick={() => remove(lead._id)}
                  className="bg-red-700 hover:bg-red-800 rounded-lg px-4 py-1 text-white font-semibold shadow-lg transition"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
