import React, { useState } from 'react';
import { createLead } from '../api';

export default function LeadForm({ onAdded }) {
  const [form, setForm] = useState({ name: '', email: '', phone: '', source: '', status: 'New' });
  const [errors, setErrors] = useState({});

  const validate = () => {
    const e = {};
    if (!form.name.trim()) e.name = 'Name is required';
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(form.email)) e.email = 'Valid email required';
    if (!/^[0-9]{10}$/.test(form.phone)) e.phone = 'Phone must be 10 digits';
    if (!form.source) e.source = 'Source is required';
    setErrors(e);
    return Object.keys(e).length === 0;
  };

  const submit = async (ev) => {
    ev.preventDefault();
    if (!validate()) return;
    try {
      const res = await createLead(form);
      setForm({ name: '', email: '', phone: '', source: '', status: 'New' });
      onAdded && onAdded(res.data);
    } catch (err) {
      console.error(err);
      alert('Failed to save lead');
    }
  };

  return (
    <form onSubmit={submit} className="bg-white p-6 rounded-lg shadow-md space-y-4 max-w-md">
      <div>
        <label htmlFor="name" className="block mb-1 font-semibold text-gray-700">Name</label>
        <input
          id="name"
          type="text"
          value={form.name}
          onChange={e => setForm({ ...form, name: e.target.value })}
          className={`w-full rounded border px-3 py-2 focus:outline-none focus:ring-2 ${
            errors.name ? 'border-red-500 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'
          }`}
          placeholder="Enter name"
        />
        {errors.name && <p className="text-red-600 text-sm mt-1">{errors.name}</p>}
      </div>

      <div>
        <label htmlFor="email" className="block mb-1 font-semibold text-gray-700">Email</label>
        <input
          id="email"
          type="email"
          value={form.email}
          onChange={e => setForm({ ...form, email: e.target.value })}
          className={`w-full rounded border px-3 py-2 focus:outline-none focus:ring-2 ${
            errors.email ? 'border-red-500 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'
          }`}
          placeholder="Enter email"
        />
        {errors.email && <p className="text-red-600 text-sm mt-1">{errors.email}</p>}
      </div>

      <div>
        <label htmlFor="phone" className="block mb-1 font-semibold text-gray-700">Phone</label>
        <input
          id="phone"
          type="text"
          value={form.phone}
          onChange={e => setForm({ ...form, phone: e.target.value })}
          className={`w-full rounded border px-3 py-2 focus:outline-none focus:ring-2 ${
            errors.phone ? 'border-red-500 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'
          }`}
          placeholder="10-digit phone"
        />
        {errors.phone && <p className="text-red-600 text-sm mt-1">{errors.phone}</p>}
      </div>

      <div>
        <label htmlFor="source" className="block mb-1 font-semibold text-gray-700">Source</label>
        <select
          id="source"
          value={form.source}
          onChange={e => setForm({ ...form, source: e.target.value })}
          className={`w-full rounded border py-2 px-3 focus:outline-none focus:ring-2 ${
            errors.source ? 'border-red-500 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'
          }`}
        >
          <option value="">Select source</option>
          <option value="Website">Website</option>
          <option value="Referral">Referral</option>
          <option value="Ad">Ad</option>
          <option value="Other">Other</option>
        </select>
        {errors.source && <p className="text-red-600 text-sm mt-1">{errors.source}</p>}
      </div>

      <div>
        <label htmlFor="status" className="block mb-1 font-semibold text-gray-700">Status</label>
        <select
          id="status"
          value={form.status}
          onChange={e => setForm({ ...form, status: e.target.value })}
          className="w-full rounded border border-gray-300 py-2 px-3 focus:outline-none focus:ring-2 focus:ring-blue-500"
        >
          <option>New</option>
          <option>Contacted</option>
          <option>Qualified</option>
        </select>
      </div>

      <div className="text-right">
        <button
          type="submit"
          className="bg-blue-600 text-white px-5 py-2 rounded shadow hover:bg-blue-700 transition"
        >
          Save Lead
        </button>
      </div>
    </form>
  );
}
